import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({
    title: '',
    description: '',
    category: '',
    location: '',
    dateLostFound: '',
    contactInfo: '',
    status: 'lost',
  });
  const [formErrors, setFormErrors] = useState({});
  const [searchKeyword, setSearchKeyword] = useState('');
  const [page, setPage] = useState(0);
  const [size] = useState(10);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(false);
  const [searchLoading, setSearchLoading] = useState(false);
  const [categoryFilter, setCategoryFilter] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    fetchItems(0);
  }, []);

  const fetchItems = async (pageNumber) => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:8080/api/items', {
        params: { page: pageNumber, size, category: categoryFilter, sort: sortOrder },
      });
      setItems(res.data.content);
      setTotalPages(res.data.totalPages);
      setPage(pageNumber);
    } catch (error) {
      console.error('Error fetching items:', error);
    } finally {
      setLoading(false);
    }
  };

  const searchItems = async (keyword, pageNumber) => {
    setSearchLoading(true);
    try {
      const res = await axios.get('http://localhost:8080/api/items/search', {
        params: { keyword, page: pageNumber, size, category: categoryFilter, sort: sortOrder },
      });
      setItems(res.data.content);
      setTotalPages(res.data.totalPages);
      setPage(pageNumber);
    } catch (error) {
      console.error('Error searching items:', error);
    } finally {
      setSearchLoading(false);
    }
  };

  const validateForm = () => {
    const errors = {};
    if (!form.title.trim()) errors.title = 'Title is required';
    if (!form.description.trim()) errors.description = 'Description is required';
    if (form.contactInfo && !/^\S+@\S+\.\S+$/.test(form.contactInfo) && !/^\+?\d{7,15}$/.test(form.contactInfo)) {
      errors.contactInfo = 'Contact info must be a valid email or phone number';
    }
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleInputChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value});
    setFormErrors({...formErrors, [e.target.name]: ''});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    try {
      await axios.post('http://localhost:8080/api/items', form);
      setForm({
        title: '',
        description: '',
        category: '',
        location: '',
        dateLostFound: '',
        contactInfo: '',
        status: 'lost',
      });
      fetchItems(0);
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  // Debounce search input
  const debounce = (func, delay) => {
    let timer;
    return (...args) => {
      if (timer) clearTimeout(timer);
      timer = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  const debouncedSearch = useCallback(
    debounce((keyword) => {
      if (!keyword) {
        fetchItems(0);
      } else {
        searchItems(keyword, 0);
      }
    }, 500),
    [categoryFilter, sortOrder]
  );

  const handleSearchChange = (e) => {
    const keyword = e.target.value;
    setSearchKeyword(keyword);
    debouncedSearch(keyword);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (!searchKeyword) {
      fetchItems(0);
    } else {
      searchItems(searchKeyword, 0);
    }
  };

  const handleClearSearch = () => {
    setSearchKeyword('');
    fetchItems(0);
  };

  const handlePageChange = (newPage) => {
    if (newPage < 0 || newPage >= totalPages) return;
    if (searchKeyword) {
      searchItems(searchKeyword, newPage);
    } else {
      fetchItems(newPage);
    }
  };

  const handleCategoryChange = (e) => {
    setCategoryFilter(e.target.value);
    fetchItems(0);
  };

  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
    fetchItems(0);
  };

  const memoizedItems = useMemo(() => items, [items]);

  return (
    <div className="App">
      <h1>Strathmore University Lost and Found</h1>

      <form onSubmit={handleSubmit} className="item-form" noValidate>
        <h2>Add Lost/Found Item</h2>
        <input type="text" name="title" placeholder="Title" value={form.title} onChange={handleInputChange} required />
        {formErrors.title && <p className="error">{formErrors.title}</p>}
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleInputChange} required />
        {formErrors.description && <p className="error">{formErrors.description}</p>}
        <input type="text" name="category" placeholder="Category" value={form.category} onChange={handleInputChange} />
        <input type="text" name="location" placeholder="Location" value={form.location} onChange={handleInputChange} />
        <input type="date" name="dateLostFound" placeholder="Date Lost/Found" value={form.dateLostFound} onChange={handleInputChange} />
        <input type="text" name="contactInfo" placeholder="Contact Info" value={form.contactInfo} onChange={handleInputChange} />
        {formErrors.contactInfo && <p className="error">{formErrors.contactInfo}</p>}
        <select name="status" value={form.status} onChange={handleInputChange}>
          <option value="lost">Lost</option>
          <option value="found">Found</option>
        </select>
        <button type="submit" disabled={loading}>Add Item</button>
      </form>

      <form onSubmit={handleSearchSubmit} className="search-form">
        <input type="text" placeholder="Search items..." value={searchKeyword} onChange={handleSearchChange} />
        <button type="submit" disabled={searchLoading}>Search</button>
        <button type="button" onClick={handleClearSearch}>Clear</button>
      </form>

      <div className="filters">
        <label>
          Category Filter:
          <input type="text" value={categoryFilter} onChange={handleCategoryChange} placeholder="Filter by category" />
        </label>
        <label>
          Sort Order:
          <select value={sortOrder} onChange={handleSortChange}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
        </label>
      </div>

      <div className="item-list">
        <h2>Lost and Found Items</h2>
        {loading || searchLoading ? (
          <p>Loading...</p>
        ) : memoizedItems.length === 0 ? (
          <p>No items found.</p>
        ) : (
          <ul>
            {memoizedItems.map(item => (
              <li key={item.id} className={item.status === 'lost' ? 'lost' : 'found'}>
                <h3>{item.title} ({item.status})</h3>
                <p><strong>Description:</strong> {item.description}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <p><strong>Location:</strong> {item.location}</p>
                <p><strong>Date:</strong> {item.dateLostFound}</p>
                <p><strong>Contact:</strong> {item.contactInfo}</p>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="pagination">
        <button onClick={() => handlePageChange(page - 1)} disabled={page === 0}>Previous</button>
        <span> Page {page + 1} of {totalPages} </span>
        <button onClick={() => handlePageChange(page + 1)} disabled={page + 1 >= totalPages}>Next</button>
      </div>
    </div>
  );
}

export default App;
