import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({
    title: '',
    description: '',
    category: '',
    location: '',
    dateLostFound: '',
    contactInfo: '',
    status: 'lost',
  });
  const [searchKeyword, setSearchKeyword] = useState('');
  const [page, setPage] = useState(0);
  const [size] = useState(10);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchItems(0);
  }, []);

  const fetchItems = async (pageNumber) => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:8080/api/items', {
        params: { page: pageNumber, size },
      });
      setItems(res.data.content);
      setTotalPages(res.data.totalPages);
      setPage(pageNumber);
    } catch (error) {
      console.error('Error fetching items:', error);
    } finally {
      setLoading(false);
    }
  };

  const searchItems = async (keyword, pageNumber) => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:8080/api/items/search', {
        params: { keyword, page: pageNumber, size },
      });
      setItems(res.data.content);
      setTotalPages(res.data.totalPages);
      setPage(pageNumber);
    } catch (error) {
      console.error('Error searching items:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/items', form);
      setForm({
        title: '',
        description: '',
        category: '',
        location: '',
        dateLostFound: '',
        contactInfo: '',
        status: 'lost',
      });
      fetchItems(0);
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  // Debounce search input
  const debounce = (func, delay) => {
    let timer;
    return (...args) => {
      if (timer) clearTimeout(timer);
      timer = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  const debouncedSearch = useCallback(
    debounce((keyword) => {
      if (!keyword) {
        fetchItems(0);
      } else {
        searchItems(keyword, 0);
      }
    }, 500),
    []
  );

  const handleSearchChange = (e) => {
    const keyword = e.target.value;
    setSearchKeyword(keyword);
    debouncedSearch(keyword);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (!searchKeyword) {
      fetchItems(0);
    } else {
      searchItems(searchKeyword, 0);
    }
  };

  const handleClearSearch = () => {
    setSearchKeyword('');
    fetchItems(0);
  };

  const handlePageChange = (newPage) => {
    if (newPage < 0 || newPage >= totalPages) return;
    if (searchKeyword) {
      searchItems(searchKeyword, newPage);
    } else {
      fetchItems(newPage);
    }
  };

  return (
    <div className="App">
      <h1>Strathmore University Lost and Found</h1>

      <form onSubmit={handleSubmit} className="item-form">
        <h2>Add Lost/Found Item</h2>
        <input type="text" name="title" placeholder="Title" value={form.title} onChange={handleInputChange} required />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleInputChange} required />
        <input type="text" name="category" placeholder="Category" value={form.category} onChange={handleInputChange} />
        <input type="text" name="location" placeholder="Location" value={form.location} onChange={handleInputChange} />
        <input type="date" name="dateLostFound" placeholder="Date Lost/Found" value={form.dateLostFound} onChange={handleInputChange} />
        <input type="text" name="contactInfo" placeholder="Contact Info" value={form.contactInfo} onChange={handleInputChange} />
        <select name="status" value={form.status} onChange={handleInputChange}>
          <option value="lost">Lost</option>
          <option value="found">Found</option>
        </select>
        <button type="submit">Add Item</button>
      </form>

      <form onSubmit={handleSearchSubmit} className="search-form">
        <input type="text" placeholder="Search items..." value={searchKeyword} onChange={handleSearchChange} />
        <button type="submit">Search</button>
        <button type="button" onClick={handleClearSearch}>Clear</button>
      </form>

      <div className="item-list">
        <h2>Lost and Found Items</h2>
        {loading ? (
          <p>Loading...</p>
        ) : items.length === 0 ? (
          <p>No items found.</p>
        ) : (
          <ul>
            {items.map(item => (
              <li key={item.id} className={item.status === 'lost' ? 'lost' : 'found'}>
                <h3>{item.title} ({item.status})</h3>
                <p><strong>Description:</strong> {item.description}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <p><strong>Location:</strong> {item.location}</p>
                <p><strong>Date:</strong> {item.dateLostFound}</p>
                <p><strong>Contact:</strong> {item.contactInfo}</p>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="pagination">
        <button onClick={() => handlePageChange(page - 1)} disabled={page === 0}>Previous</button>
        <span> Page {page + 1} of {totalPages} </span>
        <button onClick={() => handlePageChange(page + 1)} disabled={page + 1 >= totalPages}>Next</button>
      </div>
    </div>
  );
}

export default App;
